insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('138-VI-00003', 1, 5, 5, 5, 5, 1, 100, '1.Como se refleja en la vida de mujeres campesinas el continum de violencias basadas en género, en este caso la mujer era víctima de violencia física y piscológica porparte de su padre lo cual la lleva a ingresar a la guerrilla de las FARC-EP, dónde nuevamente sufre violencias basadas en género como esterilización forzada y obligada a hacer cosas que no deseaba, se escapa de la guerrilla, entregándose a las autoridades quedando a cargo del ICBF. En su vida adulta debe huir porque es perseguida por el grupo al margen de la ley por desertora. Actualmente sufre violencia intrafamiliar por parte de sus parje sentimental y padre de sus hijos. 2. Las faltas que llevaban a la guerrilla a hacer consejos de guerra y fusilar a quines hacían parte de la organización.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('138-VI-00011', 1, 5, 5, 5, 5, 1, 100, 'Proceso de participación ciudadna para  la consulta popular de Cajamarca para evitar la minería a cielo abierto impuldado por la Anglo Gold Ashanti. Papel del Estado, administraciones locales, Ejército Nacional a favor de los intereses de la empresa multinacional. Construcción del movimiento social medio ambiental del Tolima y su resistencia pacífica al proyecto minero La Colosa.   ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('138-VI-00012', 1, 5, 5, 5, 5, 1, 100, 'El papel de la academia en la construcción de la marca de Café "El Tercer Acuerdo", una marca impulsada por excombatientes de las FARC-EP, caficultores de gaitana, indígenas NASA y la ARN.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('138-VI-00014', 1, 5, 5, 5, 5, 1, 100, 'Ejercicios de defensa del territorio en medio de actores armados y capitales de empresas extranjeras (Anglo Gold Ashanti). Asesinatos en hechos confusos de líderes del movimiento social medio ambiental. Acciones y estrategias de resistencia ciudadana frente a la explotación minera a cielo abierto. Menciona reclutamiento de menores por parte de grupos guerrilleros FARC-EP presentes en la zona de Cajamarca.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('228-VI-00075', 1, 5, 5, 5, 5, 1, 100, 'El entrevistado describe como era el municipio de Cajamarca antes y después de que la multinacional AGA llegara al territorio y como este hecho desencadena una serie me persecuciones y amenazas a líderes sociales y ambientales de la zona, de igual manera se describen las dinámicas de regulación para salvaguardar la fauna y flora en el municipio por parte del grupo guerrillero de las FARC. ', '0');
