insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('592-PR-00001', 1, 5, 5, 5, 4, 1, 96, 'La entrevista muestra de manera general los distintos aspectos que configuraron la oleada de violencia instaurada por parte de los grupos armados ilegales en connivencia con las distintas fuerzas armadas del Estado. En ella, se marca la recurrencia en la violación de los derechos humanos de los pobladores del municipio de Guapi y las afectaciones al tejido social-comunitario realizadas por estos actores.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-VI-00001', 1, 5, 5, 5, 3, 1, 92, 'Con esta entrevista se entiende explicitamente como el corredor entre Bahía Solano y Cupica, se comunicaba con Riosucio, especialmente el corregimiento de Cacarica, donde se presentaron muchos hechos de violencia, desaparciones a campesinos, que tomaban ese camino para aprovechar la abundancia maderera y trabajar por el sustento de sus familias, pero así mismo, este corredor se presto para movilziación de drogas y armas.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-EE-00001', 1, 4, 5, 4, 5, 1, 92, 'Esta entrevista a sujeto colectivo étnico permitio conocer las afectaciones al consejo comunitario de COCOMAUPA, durante los ultimos 30 años, los cuales han sido dominados por los grupos paramilitares en su zona, con poca presencia guerrillera, pero siendo atropellados violentamente por los grupos paramilitares.', '0');
