insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('208-VI-00002', 1, 4, 5, 5, 3, 1, 88, 'El presente testimonio permite reconocer formas de resistencia y afrontamiento que surgen en el marco del conflicto armado, en particular la lucha de las mujeres víctimas del conflicto, que mediante sus procesos organizativos afrontan y transforman lo vivido en el marco del conflicto. igualmente, permite conocer la violencia sexual contra las mujeres atribuidas a la FARC, hecho que en el Departamento del Chocó presenta un subregistro. ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('208-VI-00003', 1, 5, 5, 4, 3, 1, 88, 'Los hechos de violencia sexual sufridos por las mujeres del municipio del Alto Baudó, especialmente en la comunidad de Chachajo, donde según el testimonio se han presentado varios hechos de violación contra mujeres a manos de grupos armados. De manera, que ofrece explicación de cómo los actores armados utilizan el cuerpo de la mujer como una arma de guerra. Igualmente, se identifican las formas como los actores armados intentaban obtener o mantener el control social y territorial en el municipio del Alto Baudó (homicidios públicos, tortura, ), el cual cuenta con uno de los registros de victimas más alto del departamento del Chocó. ', 'Aunque este testimonio no cambia la forma de como se explica este tema, si se considera trascendental por que relata hechos y contextos poco conocidos y expuestos en el Departamento del Chocó, pues, este testimonio relata múltiples infracciones al Derecho Internacional Humanitario (DIH) por parte de distintos actores armados (Fuerza pública, ELN, Paramilitares). En el caso particular de la fuerza pública se relacionan con: 1. Utilizar civiles para operaciones militares, 2. Poner en riesgo a civiles en el marco de operaciones militares, 3. Hacer objeto de ataque a una persona a sabiendas de que está fuera de combate, 4. Matar, herir o capturar a un adversario valiéndose de medios pérfidos. ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-VI-00001', 1, 5, 4, 5, 3, 1, 88, 'Entrevista colectiva realizada a niños, niñas y adolescentes de la Institución Educativa en la que cuentan sobre cambios, afectaciones y afrontamientos De situaciones violentas vividas en la verada de Chilví, narran el paso de los diferentes grupos armados, los atentados cometidos a la estación de policia con afectaciones a la escuela y afectaciones por esplosivos a niños de la vereda.; además manifiestan que muchas familias por temor a los actos violentos se desplazaron hacia tumaco y otros municipios de la costa pacifica nariñense.     Es necesario profundizar con información primaria y secundaria sobre la muerte de niños  por activación de explosivos en la vereda de Chilví. ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-CO-00155', 1, 4, 5, 5, 3, 1, 88, 'Entrevistada realizada a estudiantes de la institución Educativa Imbilí Carretera, en la que narran las dinamicas y hechos víctimizantes de los actores armados que han hecho incidencia en la zona, además cuentan ssobre los cambios, afectaciones y afrontamientos desde el hogar, la comunidad y la institución educativa.  entre los hechos se evidencian amenzas, asesinatos a militares, civiles y comerciantes, torturas y desmembramiento a familiares, impsición de normas y control del territorio, implantación de artefactos explosivos y daños al medio ambiente por dearramaniento de crudo al Rio. ', '0');
