insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('419-VI-00024', 1, 4, 5, 4, 4, 1, 88, 'Desplazamiento familiar desde Pital de la costa hacia la ciuadad de Buenaventura en el año 2009 por la presencia y presión  de actores paramilitares en el territorio  y desaparición de joven dedicado a la actividad de la pesca en Buenaventura con presunta responsabilida  de la guerrilla a cargo de alias " Don Y".Segun el testimoniante el segundo hecho esta relacionado con el retorno de su hijo desaparcido a pital de la costa en años posteriores al desplazamiento  ya que el desaparecido fue amenazado y tildado de informante por actores del frente oliver sinisterra.En el relato tambien se registran festividades religiosas como  la celebracion de la virgen del carmen y de  san antonio con balsadas que permitian unir a veredas aledañas en una misma  celebracion, con la manifestación del conflicto se evidencia que  estas celebraciones cambian.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('417-VI-00003', 1, 4, 4, 5, 4, 1, 88, 'El impacto del CAI en las prácticas  tradicionales de las comunidades negras en el corregimiento # 8 de Buenaventura. ', '0');
