insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('345-EE-00028', 1, 5, 4, 5, 3, 1, 88, 'La comunidad del Consejo Comunitario, señalo varios hechos que constituyen graves violaciones a los derechos humanos, com tambien la responsabilidad de las fuerzas armadas en los hechos de conflicto armado en el territorio.                                                                 Ademas, se recomienda indagar sobre las masacres en las comunidades de Chacón Playa y Corozal del municipio de Timbiqui. Y los intentos de abuso sexual a las mujeres del Consejo Comunitario El Cuerval.', '0');
