insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-CO-00200', 1, 4, 4, 4, 4, 1, 84, 'Entrevista colectiva a familiares víctimas de desaparición forzada durante el conflicto armado, narran la desaparición de hijos, sobrinos, esposos... en la costa pacifica nariñense y el putumayo; manifiestan que les ha tocado soportar la angustia del no saber el paradero de sus familiares desaparecidos,  a consecuencia de este hecho han padecido diferentes enfermedades, deterioro en el nucleo familiar y  el proyecto de vida.  Algunas de ellas manifiestan que pertenecen a organizaciones sociales de base, grupos de cantadoras como forma de resistencia y persistencia en la busqueda. ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-VI-00020', 1, 5, 2, 5, 4, 1, 84, 'La entrevistada narra que su familia fue víctima de atentado a su vivienda por parte del Batallon de selva numero 53 vereda del Gualtal - Tumaco, mientras la familia dormia el ejercito atacaba, por información falsa de que en el lugar se encontraban guerrileros, de este hecho resulta herida  por proyectil su hija de 16  años de edad y la misma pierde su bebe que estaba en proceso de gestación. Hasta el momento el estado no ha reconocido las resposabiliades de hecho y no ha brindado aproyo a la familia.', '0');
