insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('208-VI-00004', 1, 3, 4, 4, 4, 1, 80, 'Situación de vulnerabilidad en la que se encuentran las mujeres negras desplazadas y víctimas de otros hechos violentos (violación sexual) por el conflicto armado en el departamento del Chocó, a las cuales se les ha vulnerado sistemáticamente sus derechos humanos tanto en el territorio como en el contexto urbano. Igualmente, se identifican los actores armados y las formas como estos intentaban obtener o mantener el control social y territorial en el municipio del Alto Baudó. ', '0');
