insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('419-VI-00015', 1, 2, 3, 3, 1, 1, 56, 'La entrevistada se evidencio nerviosa al momento de la realización de la entrevista  y con respuestas limitadas ademas, manifesto no querer enfatizar en las dinamicas de los actores armado en Betania,Hormiga, Putumayo.', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('420-VI-00018', 1, 2, 2, 2, 2, 1, 52, 'El suceso mencionado en la entrevista es desaparición forzada, el hecho oecurrio en Putumayo  el  24 de febrero de 1989, la testimoniante manifiesta que la victima  selio de Tumaco en busca de mejores oportunidades, pero no corrio con la suerte debido que desaparecio junto con otras persona que tambien habian viajado a Putumayo en busca de trabajo, hasta la fecha no se  conoce la causas ni los  responsables del hecho, la familia opto por darlo por perdido, pero nunca fueron hasta el lugar  para tratar de encontrarlo, segun, no lo hiceron por miedo, por desconocimiento y por encontrarse  lejos de su tierra natal.  ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-VI-00019', 0, 2, 3, 1, 4, 1, 50, 'Mujer afrodescendiente manifiesta que fue abusada sexualmente  a sus quince años en repetidas ocasiones por un pabellon de soldados en Tamaco, no identifica responsabilidades individuales y el lugar de los hechos, reconoce que este hecho se daba siempre por hombres diferente aprovechandose de su condición mental y vulnerabilidad.  Por otro lado, cuenta que su hijo fue reclutado de la vereda Chagüi en el año 2016 por un grupo guerrilero, para el ingreso a las filas armadas en el Rio Rosario, tiempo despues fue asesinado posiblemente por colocar resistencia a su permanencia dentro del grupo. la testimoniante cuenta que padese de una patologia psiquiatrica y ésta se incrementó luego del asesinato de su hijo. ', '0');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-VI-00018', 1, 1, 1, 3, 2, 1, 48, 'Desaparición forzada de hombre afrodescendiente del municipio de Tumaco en el año 2018, posiblemente por robarle sus pertenecias, desde la fecha no saben sobre los resposables y el paradero de la víctima. ', '0');
