insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-VI-00003', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista se entiende explicitamente el conflicto armado en el municipio de Jurado, como los grupos guerrilleros y paramilitares, abusaron de los campesinos y asesinaron a grandes lideres y personas que aportaban soluciones amigables en el territorio, así mismo, los diferentes desplazamientos e intereses economicos por los cuales Juradó siempre ha sido un punto interesante para los grupos delincuenciales. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-VI-00005', 1, 5, 5, 5, 5, 1, 100, 'Esta entrevista permitio conocer la dinamica de los actores armados en cuanto a las afectaciones a la democracia y el autogobierno de los pueblos etnicos, debido a que imponian candidatos y quemaban urnas electorales a la hora de ejercer el derecho al voto. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-CO-00111', 1, 5, 5, 5, 5, 1, 100, 'La entrevista permitio conocer todas dificultades que enfrentan las mujeres en el conflicto armado, todos los maltratos, violaciones y crueldades por las que pasan en sus territorios, dejando claro cuales han sido las formas de afrontarlos y sobrevivir, con nuevas iniciativas que las han mantenido en la lucha por los derechos de las mujeres.', '');
