insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-EE-00001', 1, 4, 5, 4, 5, 1, 92, 'Esta entrevista a sujeto colectivo étnico permitio conocer las afectaciones al consejo comunitario de COCOMAUPA, durante los ultimos 30 años, los cuales han sido dominados por los grupos paramilitares en su zona, con poca presencia guerrillera, pero siendo atropellados violentamente por los grupos paramilitares.', '');
