insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('233-VI-00004', 1, 4, 4, 3, 3, 1, 76, 'Con esta entrevista se entiende explicitamente el conflicto armado en la zona del Alto Baudó, donde se presentaron muchos crimenes  que han sido invisibilizados, aun no se conocen a la luz de toda Colombia, pero han sido documentados muy pocos por las instituciones Estatales.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('208-CO-00249', 1, 4, 4, 3, 3, 1, 76, 'Restricción a los procesos organizativos y la autonomía del Pueblo Indígena Wounaan por el accionar de grupos armados que se disputan el control social y territorial en la subregión del San Juan. La relación que existe entre los grupos armados y la clase política del departamento del Chocó, que ha afectado los procesos de liderazgo de las organizaciones indígenas del pueblo Wounaan.', '');
