insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('208-VI-00001', 1, 3, 3, 3, 4, 1, 72, 'Aporta información sobre el contexto de los desplazamientos ocurridos en la costa pacífica chocoana, que provocaron el vaciamiento total de comunidades, por ejemplo: La comunidad de Chicocora sufrió dos desplazamientos masivos, el primero es el relatado en este testimonio producto de enfrentamientos entre FARC y Fuerza Pública, generó el desplazamiento de 11 familias. El segundo, entre el 2001 y 2002 por enfrentamientos entre la FARC y las AUC generó el desplazamiento de toda la comunidad que hasta la fecha continúa abandonada.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('337-VI-00002', 1, 4, 1, 5, 3, 1, 72, 'Testimonio de familiares de l niño víctima del conflicto armado a causa de utilización por parte de las FARC -EP (comandante en la fecha Alias Javier) como mensajero para el transporte de artefactos explosivos en el año 2010, en el municipio de El Charco - Nariño; cuentan sobre los cambios, afectaciones e impactos que dejó este hecho víctimizante. Los familiares manifiestan que hasta la fecha no se han reconocido responsabilidades colectivas e individuales, no hacen parte de ningun proceso de reparación por parte de las instituciones del estado.  Para ampliar información del testimonio se recomienda recolectar información a traves de entrevista con el presunto responsable del hecho. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('420-Vl-00001', 1, 4, 4, 3, 2, 1, 72, 'De acuerdo a la información proporcinada se evidencia que el hecho victimizante cometido es violencia sexual y desplazamiento forzado  a dos mujers afro de 12 y 49 año, este suceso  ocurrrio en  Miguel Nulpi  el 20 de agosto del 2007,  los responsable fueron tres integrantes de la Guerrilla, ellos llegaron a la residencia  y tomaron por la fuerza a la menor y la madre. la testimoniante cuenta que tuvo que salir el mismo dia de su vereda para tratar  de olvidar lo sucedido y para evitar que volvieran a cometer actos violento contra ellas.    Teniendo en cuenta la información se puede ver que el conflicto armado ha  acabo  con la contranqulidad de la comunidades, la gente hoy ya no se identifica como un pueblo luchador  y resistente, si no como un pueblo  que prefiere salir para evitar ser blanco de los grupos armado.   ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('420-Vl-00014', 1, 4, 4, 3, 2, 1, 72, 'La comunidad de la costa Pacifica se ha visto afecta por multiples hechos que han ocurrido durante el conflicto armado, en este caso el hecho encontrado es desplazamiento forzada a joven afrocolombiano pertenciente a la comunidad de Barbacoas Nariño, el hecho ocurrio el  año 2014, en esta epoca el joven tuvo que salir de su lugar de origen para evitar ser reclutado o asesinado por los grupos armado, a raiz del desplasamiento tuvo que migra a Pasto y desde ese entoce se encuentra viviendo en la cuidad , cuenta que no ha sido facil el nuevo cambio pero ha tratado de sobre vivir a la albersidad y a esa nueva cultura.   Teniendo en cuenta la problematica se recomienda que se les brinde oportunidad de empleo para se enfoque en este y no   tenga opción de encaminarse a tomar las armas, si no mas bien desde sus conocimiento ayuden a construir un mejor colombia.', '');
