insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('402-CO-00093', 1, 5, 5, 5, 5, 1, 100, 'Efectos del enfoque del modelo de desarrollo sobre las politicas publicas que producen y ejecutan el regimen laboral de trabjadores de la salud y las instituciones prestadoras del servicio;  Repertorio de acciones colectivas y formas de organizacion, activismo, incidencia politica, protesta y presion social por la profundizacion del derecho a la salud desde finales de la decada del 70 hasta los 90.', 'Función y papel del sector salúd en las alianzas que estructuraron el sistema de relaciones que se denominó paramilitarismo y parapolitica en Bolivar y el Caribe');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('402-CO-00097', 1, 5, 5, 5, 5, 1, 100, 'Barranquilla sirvió de receptor de poblaciones en condición de desplazamiento procedentes de todos los departamentos del caribe y de los principales casos emblemáticos de despojo y desplazamiento, desde mediados de los 90 en adelante y hasta la actualidad; La excesiva concentración del poder politico y economico en la sociedad barranquillera y atalnticense, es considerada por los asistentes como un efecto directo del conflicto armado reciente', 'Los diferentes tipos de organizaciones que intentaron hacer atencion a victimas de la violencia incluso antes de las leyes y politicas publicas diseñadas para tal fin, expresan un proceso de resistencia ya frontamiento al conflicto armado, merecedor de reconocimiento en la historia regional; ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00014', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista es posible elaborar una red de relaciones entre el centro y norte de Bolívar y el departamento del Atlántico.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00019', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista es posible analizar las relaciones que se tejen entre el paramilitarismo y el sector salud a través del caso de Mutual Ser', 'Con esta entrevista cambió mi percepción sobre la constante persecución y agresiones físicas que sufren las mujeres por parte de la misma fuerza pública');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00021', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista es posible analizar procesos de reconciliación entre responsables y víctimas que ya llevan un proceso adelantado', 'El conflicto en municipios y corregimientos como Ballestas, Bolívar');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-PR-00159', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista es posible conocer la estructura organizativa de los actores paramilitares que hicieron presencia en el barrio Nelson Mandela así como las diferentes organizaciones sociales y proyectos que nacieron y han trabajado en ese contexto', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-PR-00166', 1, 5, 5, 5, 5, 1, 100, 'A través de esta entrevista es posible obtener aspectos relevantes desde la génesis del conflicto en el departamento de Bolívar, su profundización y su relación con actores y casas políticas', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00004', 1, 5, 5, 5, 5, 1, 100, 'Con esta entrevista se permite entender explícitamente el contexto de la Universidad del Atlántico. Es posible conocer los diferentes actores en su interior y aquellos que, desde afuera, han tenido incidencia en ella', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('203-PR-00198', 1, 5, 5, 5, 5, 1, 100, 'Reconocer los más graves patrones de violencia que se dieron en el marco de la guerra en la Troncal del Caribe sector Guachaca, Don Diego. Identificar los principales factores de impactos y nuevos hechos de violencia en el marco del acceso a la justicia, la movilización social y la partición en política.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('304-VI-00004', 1, 5, 5, 5, 5, 1, 100, 'Afectación de comunidades afrodescendientes de la zona norte del Magdalena a causa de los grupos paramilitares y actores económicos de la agroindustria. Riesgos y resistencias de las comunidades afros ante el cuidado ambiental del territorio. En medio de los rezagos sociales y militares del paramilitarismo. Historia de poblamiento de las comunidades afrocampesinas del municipio Zona Bananera', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('304-VI-00006', 1, 5, 5, 5, 5, 1, 100, 'Afectación de los pueblos palafitos de la Ciénaga grande de Santa Marta en el marco de la incursión paramilitar del Bloque Norte - Afectaciones y dinámicas alrededor del complejo ambiental Ciénaga grande y la pesca - Actores económicos y conflicto armado sobre territorio acuífero', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('304-VI-00011', 1, 5, 5, 5, 5, 1, 100, 'Graves violaciones sobre población campesina: Pillaje, confinamiento, despojo y violencias sexuales - Violencias sexuales ejercidas contra hombres campesinos heterosexuales', '');
