insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('542-VI-00018', 1, 5, 5, 5, 5, 1, 100, 'La comunidad Tamaquito 2 vivió un proceso de reasentamiento en La Guajira, por parte de la empresa Cerrejón, con el fin que en su territorio se hiciera explotación minera. Para este fin, según el testimonio registrado, los medios utilizados para sacarlos del territorio fue a través de la contaminación de la naturaleza, el bloqueo de vías de salida y entrada por parte de la misma compaía y el Ejército, además de la posible presencia de un grupo armado que estaba al servicio de la multinacional. Es necesario explicar qué ha pasado con con esta población ahora, teniendo en cuenta que en donde se encuentran no cuentan con la riqueza de fauna y flora y tranquilidad que tenían, afectando su alimentación, sus tradiciones y sus proyectos de vida personales y colectivos. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00050', 1, 5, 4, 5, 5, 1, 96, 'Tema clave: Prácticas de reclutamiento forzado por parte de las milicias del 35 frente de las FARC en zona rural del municipio de Morroa, acciones que se dieron en asocio con familiares del menor que iba a ser reclutado. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00071', 1, 5, 5, 5, 4, 1, 96, 'Tema clave: fuertes resentimiento entre la población de civil de Coloso por cuanto las víctimas del conflicto están conviviendo con quienes fueron sus victimarios en el pasado.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('144-VI-00029', 1, 5, 4, 5, 5, 1, 96, 'La entrevistada vio la necesidad de estudiar psicología para poder atender de una mejor manera a sus alumnos menores que tenían secuelas a causa del conflicto armado en San Juan de Nepomuceno.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('474-PR-00001', 1, 5, 5, 5, 4, 1, 96, 'El papel del Estado en el conflicto armado interno y sus dinámicas en el sur de Atlántico y Montes de María. Además, se puede realizar un acercamiento a la alianza entre la fuerza pública, los grupos paramilitares y los terceros civiles. Asimismo, se permite mirar el proceso de desarticulación y debilitamiento de la organización campesina a causa del conflicto. ', 'Las formas de afrontamiento y resistencia que son particulares en el entrevistado en cuanto a que hace referencia a la clandestinidad a ese estado no asosiado con la vinculación a un actor armado sino con la necesidad que tuvo de ocultarse desconectacndose del mundo y hasta de su familia para preservar su vida. Con esto se confronta el lugar común existente al momento de asoaciar a la clandestinidad con la acción política insurgente. ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('205-VI-00004', 1, 5, 5, 5, 4, 1, 96, 'El control ejercido por el paramilitarismo en el depeartamento del Magdalena que les permitió incluso controlar vías principales, permear entidades del estado y re establecer un orden social.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('203-VI-00005', 1, 5, 5, 5, 4, 1, 96, 'Despojo de tierra, ganado y circuitos comerciales a ganaderos por parte de paramilitares con fines de concentración económica en elites políticas y ganaderas articuladas a la estructura armada en Salamina, Pivijay y Media Luna', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('304-VI-00001', 1, 5, 5, 5, 4, 1, 96, 'Violencias sexuales y fenómeno del paramilitarismo- Secuestro con fines sexuales como modalidad de violencia. - Contextos de riesgo y vulnerabilidad de las mujeres y menores de edad en medio del patrullaje paramilitar ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('331-CO-00301', 1, 5, 5, 5, 4, 1, 96, 'Entrevista colectiva a campesinos vinculados al PNIS, aportan al contexto en relación con la presencia de responsables, ponen en claro el tema de la Coca y del cultivo de coca  en su territorio y desarrollan  ampliamente sus afectaciones y resistencias  como del campesinado del Alto Sinú con  ocasión del conflicto armado. ', 'Ahora comprendo que el cultivo de la  coca es una externalidad del campesinado del Alto Sinú, no tiene que ver son sus tradiciones y cultura');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('331-CO-00302', 1, 5, 5, 5, 4, 1, 96, 'Entrevista colectiva  con  campesinado  del Alto Sinú  articulado a Juntas de acción comunal  y vinculado al proceso de erradicación voluntaria, en su mayoría victimas del conflicto y victimas del despojo de tierras por parte del Estado mediante la creación del parque y de la Ley Segunda.  Abordan hechos victimizantes mediante linea de tiempo desde los años 50,  los afrontamento y resistencias, las causas, respensables y beneficiarios de esta violencia y explican ampliamente los impactos. ', 'Me es claro que el cultivo de la coca fue la unica opción de sobrevivencia que tuvieron los campesinos del Alto Sinú despues del llenado de la represa de Urrá. Estaban arruinados. No quieren ese cultivo, no es parte de su cultura');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00009', 1, 5, 5, 4, 5, 1, 96, 'Es una entrevista colectiva con lideres y lideresas de ONGs que hicieron parte del Grupo Interinstitucional de apoyo a Desplazados GIDAD el cual debió disolverse por amenzas y hostigamientos a varios de sus miembros. Las entrevistadas y el entrevistado trataron  de construir una linea de tiempo de hechos victimizantes que combinaron  con las dinámicas del conflicto armado en Córdoba entre 1987 y 2001,  se refieren a responsables, a las respuestas institucionales y a la estigmatización y señalamiento  que hicieron de las ONGs que entonces trabajaban con población desplazada y de los afrontamientos y resistecias que debieron implementar algunas que pasaron a acompañar los casos emblematicos de reunicación con acceso a tierra de mujeres victimas. Varias ONGs tuvieron que dejar de funcionar por las amenzas o asesinaros de algunos de sus miembros importantes.  Aportan lecciones y aprendizajes para la no repetición.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00067', 1, 5, 4, 5, 5, 1, 96, 'Es una entrevista colectiva,  organizada con la ANUC departamental, con  campesinos victimas del conflicto armado,  lideres y lideresas de Loma Verde, Canalete, Valencia y Monteria. Construyen una linea de tiempo de hechos victimizantes y afrontamientos  en cada uno de los territorios que representan desde 1964 hasta la actualidad; explican lo que significó en cada uno de sus territorios la presencia de la guerrilla y la orden de aniquilamiento de la ANUC por parte de los paramilitares. Describen las causas y los responsables y precisan quienes se benefician del despojo y del aniquilamiento campesino; de manera amplia tratan los impactos con perspectiva de genero, y aportan aprendizajes e  ideas para la no repetición', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00084', 1, 5, 4, 5, 5, 1, 96, 'Es una entrevista colectiva con adolescentes y jovenes victimas del Conflicto Armado del corregimiento de Tierra Adentro, integrantes del Nodo Montelibano de la Red de jovenes del Sur de Córdoba (zona PDET). La entrevista  aborda  la realidad juvenil desde una perspectiva de género y  de ciclo de vida, sobre las  dinámicas del conflicto, los  hechos victimizantes que les han ocurrido, las acciones de afrontamiento de sus familias y comunidades, sobre los  impactos en sus vidas  y  tambien sobre los aprendizajes que sacan de toda esta dolorosa experiencia. Hacen aportes sobre el contexto de un  territorio como Tierra Adentro azotado por la  presencia primero del EPL, luego de la FARC y de los paramilitares  y relatan lo que vivieron con los ataques entre estos grupos y las masacres.  Aportan  aspectos relevantes como  la Débil capacidad e iniciativa de los jóvenes para organizarse;  el temor infundido o heredado  para asumir liderazgos y participar de espacios colectivos; las afectaciones en el desarrollo de la personalidad de los jóvenes: psicológicos, emocionales y comportamentales todo lo cual les dificultad  visionar un proyecto de vida desde la legalidad.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00085', 1, 5, 4, 5, 5, 1, 96, 'Es una entrevista colectiva de adolescentes y jovenes del municipio de Puerto Libertador, en la que ellos y ellas reconstruyen en lo posible,  de forma oral, colectiva  y contextual,  su verdad.  Parten de una línea de tiempo en la que según su procedencia describen los hechos  victimizantes que las y los fectaron y tambien  los escasos  afrotamientos que en relación con ellos se dieron.  Hacen esfuerzos por contextulizar y plantean causas y se refieren a quienes se benefician con esta violencia, explican  ampliamente los impactos del conflicto sobre ellos y ellas considerados desde el enfoque étnico, de género y diferencial y finalmente aportan lecciones aprendidas para la no repetición. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00252', 1, 5, 4, 5, 5, 1, 96, 'Es una entrevista colectiva a campesinos y campesinas de la ANUC Valencia, mediante linea de tiempo entre 1970- 2019 elaboradas  por grupos geograficos que  describen los hechos victimizantes ocurridos en Valencia ocasionados tanto por la guerrila como por los paramilitares y los enfrentamentos entre estos, tambien precisan las responsabilidades de las autoridades y de la fuerza pública, precisan tambien las causas y los beneficiarios de la violencia en un territorio controlado por Don Berna y desde una perspectiva de genero y teniendo en cuenta el ciclo de vida abordan los impactos. Se refieren tambien a los afrontamientos y las resistencias siendo que se trataba del exterminio de la ANUC, de asegurar el despojo  y  de impedir las reclamaciones de tierra. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('333-CO-00306', 1, 5, 4, 5, 5, 1, 96, 'Es una entrevista colectiva con adolescentes y jovenes victimas de las zonas PDET  de Valencia e integrantes de la Red de Jovenes del Sur de Córdoba.  Tratan los hechos victimizantes ocurridos en sus comunidades, lo que le ocurre a las y los adolescentes y jovenes  y los impactos que ellos les producen, precisan el grupo armado responsable y tambien aportan a las causas y los factores de persistencia. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('237-VI-00024', 1, 5, 5, 5, 4, 1, 96, 'Existió connivencia entre paramilitares y periodistas de la región, y esta connivencia fue utilizada por comunicadores para afectar a otros colegas. El periodista de la entrevista estuvo en riesgo de ser asesinado y debió desplazarse, sufriendo las consecuencias del desarraigo, la ausencia de su familia y la falta de opciones para sustentarse, debido a que una colega lo indispuso con un jefe paramilitar de la zona. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('237-VI-00021', 1, 5, 5, 5, 4, 1, 96, 'Para los poetas y artistas las afectaciones derivadas del conflicto armado tienen connotaciones más intensas en el campo del alma, que en cualquier otra dimensión. A este cantautor le fue fragmentada la conexión con su cultura, sus costumbres, su modo de vida, su apego al territorio como determinador de su identidad. Estas afectaciones, sus ausencias y nostalgias han venido quedando plasmadas en su obra musical.   La victimización con ocasión del conflicto armado acentuó en muchos artistas la necesidad de crear; los procesos creativos fueron afectados, transformados, alimentados.  "La ausencia generadora de nostalgias es el motor productor del arte de los que son como yo: geoafectivos, huérfanos, buscando ser queridos", dice el entrevistado.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('237-VI-00067', 1, 5, 5, 5, 4, 1, 96, 'Esta entrevista resulta de suma importancia para comprender la dinámica de penetración de las empresas mineras a las comunidades asentadas sobre terrenos de interés para la explotación minera, pues muestra cómo personal de la empresa llegó amistosamente a las comunidades, se hizo amigo de ellos al extremo de baitizarles a los hijos, de modo que se hicieron de compadres en las comunidades; pero esta información íntima de la gente fue la msma que itilizaron para sacarlas después de su territorio. La comunidad afrodescendiente de Tabaco confió en la palabra de los foráneos porque para ellos la palabra tiene más valor que un contrato firmado; no obstante, salieron engañados y fueron sacados violentamente mediante una diligencia de desalojo liderado por instituciones del Estado, en el cual torturaron a muchas personas, destruyeron con máquinas las viviendas. Los impactos des desarraigo han sido devastadores y transformadores.  Hoy la comunidad de Tabaco vive el desarraigo, aferrándose a no dejar morir tradiciones como sus artesanías, sus prácticas culinarias, su sabiduría ancestral para curar enfermedades y despedir a los muertos. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('237-VI-00066', 1, 5, 5, 5, 4, 1, 96, 'En la comundad de El Rocío, en Albania/La Guajira, viven indígenas wayuu que han resistido a pesar del despojo, el desplazamiento, las amenazas y el confinamiento de que han sido víctimas, por estar asentados en zona de interés para la explotación carbonífera de la empresa Cerrejón, que cuando llegó al territorio tenía por nombre Intercor, y de ella hacía parte el Estado colombiano. Al Estado responsabilizan estas comunidades por atorgar licencias y no obligar al cumplimiento de los fallos que favorecen a la comunidad. La actividad minera anuló la soberanía alimentaria de esta comunidad, que ya no puede cazar, ni cultivar, ni pescar en el arroyo Bruno, una de las fuentes de vida de la gente ahí; este arroyo fue desviado por la empresa, que contrata a personas de la misma comunidad para que se encarguen de hacer mantenimiento al desvío, generando así el dilema ético y moral de trabajar con la empresa que les está causando el daño, por no tener otra opción de sustento. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('542-VI-00015', 1, 5, 5, 5, 4, 1, 96, 'El entrevistado ofrece una mirada al impacto que generó en él y en su comunidad indígena (kankuama) la estigmatización que se produjo al señalar a todos los miembros de esta población colaboradores de la subversión. Este testimonio puede contribuir a profundizar los impactos y afrontamientos que vivió esta comunidad durante el conflicto y cómo la ha marcado hasta la ctualidad. El testimonio también ofrece detalles para estimar la posible acción de fuerzas del estado en su contra debido a la estigmatización. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00032', 1, 5, 5, 4, 4, 1, 92, '*Cooptación de recursos del hospital por parte de actores armados / Afectaciones a los Derechos Economicos, sociales y culturales  *Fuerza publica y clanes politicos en connivencia con el paramilitarismo  *Paramilitarismo al servicio los intereses politicos y económicos de los grupos politicos.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00036', 1, 5, 4, 5, 4, 1, 92, '*La guerrilla realizó ataques indiscriminados, afectando civiles.  *Vulnerabilidad de personas que ejercen oficios que implican movimientos/ desplazamiento por el territorio.  *Conflictos personales entre miembros de la comunidad que se resuelven por medio de los actores armados ilegales.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00045', 1, 5, 5, 5, 3, 1, 92, 'Transformaciones de los conflictos vecinales en escenarios de violencia  a partir de la llegada de milicia guerrillera. ', '');
