insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00009', 1, 4, 5, 4, 5, 1, 92, 'Los procesos de resistencia desempeñados por mujeres al interior de la mesa de víctimas de Cartagena', 'La situación de seguridad y la incursión de grupos paramilitares al barrio El Pozón de Cartagena');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('399-VI-00015', 1, 5, 5, 4, 4, 1, 92, 'El rol desempeñado por los diferentes líderes sociales de Nelson Mandela y el por qué representaban una amenaza para los actores paramilitares', 'Con esta entrevista cambió explícitamente mi percepción respecto al barrio Nelson Mandela de Cartagena. Es decir, a partir de ahí, empiezo a entender que lo que allí sucedió y aún sucede va mucho más allá de la delincuencia común. Todo lo que allí sucede responde a las estructuras paramilitares que ahí se instauraron');
