insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00009', 1, 5, 4, 5, 4, 0, 82, 'Tema Clave: La utilización de las relaciones afectivas por parte de mujeres combatientes con hombres que estuvieran categorizados con un alto nivel poder al interior de las FARC, como estrategia para ser exoneradas de ciertas labores pesadas, pero, sobre todo, la protección física que les generaba este “status”, en el entendido que ningún hombre las podría obligar a tener relaciones sexuales.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00041', 1, 4, 4, 4, 3, 1, 80, 'La condiciòn de familiares de menores reclutados como conadyudante en procesos de detenciòn arbitraria en comunidades altamente estigmatizadas por la presencia de grupos guerrilleros.  Las dinamicas de estigmatizaciòn de la poblaciòn por parte de la fuerza pùblica.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00055', 1, 3, 4, 4, 4, 1, 80, 'Tema clave: Persecución y estigmatización de líderes campesinos que adelantaron procesos de recuperación de tierra en Coloso, por parte la fuerza pública, terratenientes y paramilitares, situación que desencadeno desplazamientos, homicidios y detenciones injustas. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('144-VI-00032', 1, 4, 4, 4, 3, 1, 80, 'Capturas ilegales en Colosó a población civil por parte de la fuerza pública con el fin de demostrar resultados positivos en el marco del conflicto armado.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00009', 1, 4, 2, 5, 4, 1, 80, '*Violencia contra las mujeres y NNA como estrategia de control social y económico.   *Las creencias religiosas que obligan a las mujeres a asumir embarazos productos de violencias sexuales. En este caso el hijo de la entrevistada está en condición de discapacidad desde el nacimiento, probablemente asociada a la falta de controles médicos y al abuso sexual de forma violenta (no se sabe en qué momento del secuestro, entre los abusos sexuales, queda en embarazo), el hijo tiene los apellidos de un conocido que lo registra como hijo (aparece como el padre) para evitar aparecer como madre soltera, fue una manera de la familia de afrontar "la vergüenza" de que el hijo no tuviera papá.  ', '   *Nacimiento de hijos en condición de discapacidad posiblemente asociado al inicio de la gestación en un contexto de violencia extrema (ver articulo: ESTRÉS PRENATAL Y SUS EFECTOS SOBRE EL NEURODESARROLLO en https://www.sciencedirect.com/science/article/pii/S0716864016300554 ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00012', 1, 4, 3, 4, 4, 1, 80, '*La verdad judicial obtenida en procesos como el de Justicia y Paz,  no se corresponde con la verdad que esperan las victimas desde el vínculo afectivo con el desaparecido.', '*Muchas víctimas no están preparadas para reconocer-aceptar-considerar que su familiar se dedicaba a una actividad posiblemente ilegal.  Puede ser por temor a ser juzgado, a perder derechos o por el deseo de limpiar la memoria/recordar dignamente a su ser querido.');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00018', 1, 4, 4, 3, 4, 1, 80, '*Corrupción al interior de las instituciones del estado.  *Participación de civiles en actividades judiciales sin su autorización.', '');
