insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00041', 1, 4, 4, 4, 3, 1, 80, 'Tema clave: Poca oferta institucional en materia de atención psicosocial a víctimas del conflicto para afrontar la perdida y reorientar sus proyectos personales que se vieron alterados al momento de la ocurrencia de los hechos violentos. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00046', 1, 4, 3, 5, 3, 1, 80, 'Tema clave: La necesidad que tienen las víctimas del conflicto armado por saber la verdad y de esta manera cerrar ciclos de dolor que aún permanecen abierto.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00052', 1, 4, 3, 4, 4, 1, 80, 'Tema clave: Control hegemónico del 35 frente de las FARC sobre la municipalidad de Coloso, donde la administración local debía someterse a las indicaciones que estos entregaban, sobre todo en los temas presupuestales.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00053', 1, 4, 3, 5, 3, 1, 80, '  Tema clave: Falta de acompañamiento en materia de atención psicosocial a víctimas del conflicto armado por parte de las instituciones del estado colombiano que fueron creadas para promover y garantizar el restablecimiento de los derechos de esta población.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00054', 1, 4, 4, 4, 3, 1, 80, 'Tema clave:  La estigmatización de la fuerza pública a familiares de las parejas de los milicianos del 35 frente de las FARC, lo que dio lugar a operativos ilegales en las propiedades de varias personas en el municipio de Coloso, situación que fue originando resentimientos entre población civil y fuerza pública.   ', '');
