insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00023', 1, 3, 4, 4, 3, 1, 76, '*Fragmentación social, desconfianza y polarización al interior de las comunidades asociada a la presencia de actores armados - milicianos.  Persiste en el tiempo.  *Miembros de la comunidad que acompañan y aumentan el poder de los paramilitares en las comunidades - Lo consideran como "necesario" para "Limpiar" la comunidad.   *Bajo impacto de las acciones de asistencia, atención psicosocial. ', '*Disposición de las comunidades de no aceptar nuevamente la presencia de actores armados en su territorio. ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('144-VI-00025', 1, 4, 3, 4, 3, 1, 76, 'Desmovilización de la Corriente de Renovación Socialista en Flor del Monte, provocó que sus pobladores sean estigmatizados como guerrilleros.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('144-VI-00037', 1, 4, 4, 3, 3, 1, 76, 'Abandono de la fuerza pública desde 1996 hasta 2004 en Colosó, a partir del "burro bomba" en Chalán, propició que grupos armados se instalaran en la zona.  Reuniones del Eln en 1990 para reclutar personal en Colosó, se hicieron bajo el pretexto de que se trataría de las tomas de tierras y luchas campesinas.  Según el entrevistado, el asesinato de su esposa, suegro y cuñados por parte de las Farc y Eln se debió a que gente del pueblo los mal informaban.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00002', 1, 4, 3, 4, 3, 1, 76, '*Relación cotidiana de las comunidades con actores armados ilegales, quienes ejercian la autoridad y control social.  *Conflictos personales entre miembros de la comunidad que se resuelven por medio de los actores armados ilegales.', '*Comunidades cuyas dinámicas y prácticas sociales  asociadas a la resolución violenta de conflicto, discriminación, ilegalidad y corrupción.');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00022', 1, 4, 3, 4, 3, 1, 76, '  *Disputas territoriales entre actores del conflicto e intereses estratégicos asociados al control territorial.  *Las personas esperan verdad y justicia, pero no comprenden los procesos judiciales en los que están participando.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-000010', 1, 5, 4, 4, 1, 1, 76, 'Fenomeno del paramilitarismo. Desapariciòn forzada de comerciantes como estrategia de control territorial ( posibles informantes) ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00021', 1, 5, 4, 4, 1, 1, 76, 'Paramilitarismo: Masacres y participaciòn de la fuerza publica . Fuerza publica: estigmatizaciòn, persecuciòn y desplazamiento forzado de a campesinos. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00028', 1, 5, 4, 4, 1, 1, 76, 'Paramilitarismo: Masacres y participaciòn de la fuerza publica. Guerrilla: formas de coptaciòn de la poblaciòn, homicidios selectivos. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00034', 1, 4, 4, 5, 1, 1, 76, 'Desapariciòn forzada de docentes lideres sindicales. Procesos de resistencia, resilencia y reconstrucciòn del tejido social.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('131-VI-00039', 1, 4, 4, 4, 2, 1, 76, 'La historica dinamica de movilidad humana entre Colombia y Venezuela pudo contribuir para que algunas victimas no se autodenominen exiliados. Esto quizas podria conllevar al subregistro de este tipo de hecho dada la naturalizaciòn del conflicto en la dinamica de movilidad transfronteriza. ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('144-VI-00013', 1, 4, 3, 4, 3, 1, 76, 'Control social ejercido por el frente 37 de las Farc en Huamanga produjo desplazamiento masivo de sus pobladores.  El entrevistado manifiesta no querer volver a Huamanga porque económicamente le es más rentable vivir en Sincelejo.  Paramilitares asesinan por falsos señalamientos a un hermano del entrevistado en El Carmen De Bolívar', '');
