insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00033', 1, 5, 4, 3, 2, 1, 76, 'Tema clave: La estigmatización como estrategia de la fuerza pública para justificar sus operaciones militares en los poblados donde tenía mayor presencia los grupos insurgentes, lo que desencadeno la detención arbitraria de muchos campesinos y campesinas, caso corregimiento Las piedras- Toluviejo.', '');
