insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('136-VI-00058', 1, 4, 4, 4, 2, 1, 76, 'Tema clave: Disputas territoriales entre paramilitares pertenecientes al frente golfo del morrosquillo y miembros del 35 frente de las FARC en zona rural de Toluviejo, situación se vio reflejada en los homicidios selectivos por parte de las AUC a pobladores que supuestamente colaboraban con la Guerrilla, y a su vez, los homicidios ejecutados por las FARC, en respuesta al asesinato de familiares de milicianos y combatientes que operaban en la zona.', '');
