insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('241-VI-00006', 1, 4, 4, 3, 1, 1, 68, 'Relación de estudiantes que fueron asesinados durante la toma paramilitar y su ejercicio en la militancia política al interior del Partido Comunista Colombiano y la Juventud Comunista en el departamento de Córdoba.', 'La corrupción es considerada una categoría central para entender el proceso de captura paramilitar a la Universidad de Córdoba y cómo el movimiento estudiantil reivindicó en la década de 1990 la lucha frontal contra las redes clientelares en la institución.');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('057-VI-00014', 1, 3, 3, 4, 4, 0, 66, '*El largo tiempo de espera de noticias acerca de la desaparición, tiene un fuerte impacto y desgaste emocional.', '*Muchas víctimas no están preparadas para reconocer-aceptar-considerar que si familiar se dedicaba a una actividad posiblemente ilegal.  Puede ser por temor a ser juzgado, a perder derechos o por el deseo de limpiar la memoria/recordar dignamente a su ser querido.');
