insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('176-VI-00019', 1, 5, 5, 4, 4, 1, 92, 'Dinámicas y formas de acción de grupos paramilitares. El relato contiene hechos de violencia sexual, retención forzada y homicidio. Se señalan algunos lugares de permanencia del actores paramilitares en veredas y los corredores de movilidad.', '');
