insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('198-VI-00006', 1, 4, 4, 5, 5, 1, 92, 'Permite conocer las afectaciones y el proceso de resistencia que han tenido las comunidades indígenas. Así mismo  describe los patrones de violencia ejercidos por el Ejército colombiano y los grupos guerrilleros a las comunidades , de igual manera explica el accionar de los grupos guerrilleros en el reclutamiento de menores indígenas a las filas  y  de la desaparición forzada.', '');
