insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('176-VI-00024', 1, 5, 4, 4, 4, 1, 88, 'El relato señala una desaparición forzada por parte de paramilitares en relación con agentes del Estado. Señala elementos de tortura a la víctima, el desmembramiento para desaparecer a la víctima y la persecución que tuvo que pasar la familia tras haber denunciado y que no hubiesen habido avances en términos de justicia.', '');
