insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('198-VI-00010', 1, 5, 4, 4, 2, 1, 80, 'Los  impactos causados por la desaparición, en un contexto  en el cual lo único que podían hacer era quedarse calladas para no ser señaladas y amenazadas y por el temor a que se les desapareciera otro miembro de su familiar ,dado al control territorio que ejercían los grupos perpetradores de los hechos  y por otro lado el  abandono estatal y la falta de garantías en el proceso de búsqueda   ', 'Esta entrevista no permitió un cambio de punto de vista frente a la dinámica territorial  y los conflictos ');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('198-VI-00001', 1, 5, 4, 4, 2, 1, 80, ' El afrontamiento y resistencia que han tenido   para afrontar el daño y responder a los duros cambios provocados en sus vidas, dado a la poca ayuda institucional  y mecanismos de búsqueda de sus familiares y para atender o reparar los daños causados, ha obligado a que sean ellas mismas las que deban hacerse cargo de acciones que debieran estar en cabeza del estado y de la sociedad colombiana y por ello se han organizado creando una asociación a la que actualmente pertenecen.', 'Esta entrevista no permitió un cambio de punto de vista frente a la dinámica territorial  y los conflictos ');
