insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('168-VI-00001', 1, 3, 3, 4, 4, 1, 76, 'De acuerdo a la información aportada por el testimonio entre 1999 y 2004, en Cúcuta y su área metropolitana hicieron presencia grupos paramilitares, durante este periodo se presentaron múltiples masacres, asesinatos selectivos, desapariciones, amenazas y las mal llamadas limpiezas sociales en sectores marginados de la ciudad, donde a través de panfletos se declaraban objetivo militar a gays, lesbianas, bisexuales y transgeneristas, vendedores de droga, ladrones, indigentes, vendedores ambulantes, quienes eran exterminados por ser considerados indeseables o desechables para la sociedad, a esta lista de indeseable también se sumaban colaboradores y miembros del Eln, quienes también tenían una fuerte presencia en el territorio y las FARC, a esta dinámica se suma a la instalación de los hornos crematorios en Juan Frío entre 2001 y 2003 por parte de las AUC, comandadas por Jorge Iván Laverde Zapata, alias El Iguano, a donde llevaban a sus víctimas y eran torturadas e incineradas sin dejar rastro alguno de sus cuerpos, el testimonio sospecha que a su hermano diagnosticado con VIH fue llevado y desaparecido allí.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('168-VI-00008', 1, 3, 3, 4, 4, 1, 76, 'Podria no aportar muchos elementos de contexto sobre lo que ocurria durante ese periodo de incrusión paramilitar en la ciudad, pero se suma a uno de los muchos casos de asesinatos sistemáticos a vigilantes en Cúcuta y su área metropolitana.', '');
