insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('253-VI-00012', 1, 4, 5, 3, 2, 1, 76, 'Entrevista importante para el proceso con Internados.  Experiencias-rol de los docentes en el marco del conflicto; uso de internados por parte de actores armados – enfrentamientos Ejército - FACR (en la Mecha); Relación paramilitarismo – ejercito – guerrilla; prácticas de los distintos actores; Estigmatización social por origen de nacimiento; señalamiento; homicidios; Reclutamiento forzado (caso del internado); control social y territorial de los actores armados; periodos de mayor presencia; formas de resistencia.  Área: Lejanías, Meta.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('253-VI-00020', 1, 4, 5, 4, 1, 1, 76, 'FARC: Reclutamiento forzado (caso 1997 – 1999); ubicación áreas de entrenamiento y presencia lejanías y el Castillo - normas – formación militar y política - vida intrafilas - asesinatos (colectiva y caso del papá) y caso de desaparición forzada (Ubicación) – reuniones con las masas – Brus (pistolero de las FARC) – Extorsión; Amenazas, confinamiento (2003 – 2005) despojo y desplazamiento forzado.  Área: El Castillo y Lejanías, Meta.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('253-PR-00001', 1, 4, 5, 4, 1, 1, 76, 'El entrevistado no relaciona con fechas la información que suministra.   Aporta información relacionada a iniciativas de resistencia: Cuerpo de bomberos voluntarios del municipio de lejanías; experiencia de los docentes alrededor del conflicto: Reclutamiento forzado; Reunión de los docentes por parte de FARC para extorsión; FARC: Entrada al municipio, hostigamientos, normas y multas, casos de víctimas, estrategias de reclutamiento, toma de lejanías; Autodefensas: Entrada al municipio, Víctimas, reclutamiento, extorsión; Fuerza pública y militares: Capitán Garzón; daño a bienes civiles y al ambiente; Otros actores: Henry Beltrán (Mención); Cultivos de uso ilícito: Presencia más marcada hacia las veredas La Profunda, Las Delicias, Guarumales, Miravalles del Guejar, Aguabonita.  Área: Lejanías.  ', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('253-VI-00022', 1, 4, 5, 4, 1, 1, 76, 'Relata hechos de señalamiento, desplazamiento forzado, trabajo forzado, extorsión, amenazas. Dinámica de FARC / Paramilitares / Coca. En Caquetá, Meta y Vichada.', '');
insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('457-VI-00016', 1, 4, 4, 4, 2, 1, 76, 'Casos que aborda: niño bomba vista hermosa, estructura FARC, tambien hace alusión a Represion a movimientos religiosos. Se recomienda priorizar dado que fue lider de un movimiento religioso antes durante y despues del despeje, y narra en detalle la persecucion que vivieron la iglesias protestantes en la zona. Asi mismo, menciona acerca de comandantes de las FARC en vista hermosa, y las violaciones de DDHH que cometieron en el marco de CAI ', '');
