insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('447-PR-00328', 1, 4, 4, 3, 3, 1, 76, 'Esta entrevista primero se subio como individual, pero se perdian detalles importantes por lo que se decidio sistematizar como a profundidad, ofrece una explicación sobre el ataque de los grupos armados a la misión médica. ', '');
