insert into prioridad(codigo, fluidez,d_hecho, d_contexto, d_impacto, d_justicia, cierre, ponderacion, ahora_entiendo, cambio_perspectiva) values ('142-PR-00398', 1, 5, 5, 1, 1, 0, 58, 'Mayor del Ejército condenado por concierto para delinquir con grupos paramilitares. Su testimonio es clave para entender el impacto de la Escuela de las Américas en la lucha contrainsurgente y la importancia de apoyo de Estados Unidos en la estrategía de guerra en Caquetá', '');
