update esclarecimiento.casos_informes set titulo='Informe sobre el desplazamiento forzado de la junta de gobierno del consejo comunitario de Rescate las Varas', autor='Consultoría para los Derechos Humanos y el Desplazamiento - CODHES', descripcion='Presenta un balance sobre la situación humanitaria de la Junta de Gobierno del Consejo Comunitario Rescate las Varas, forzados a desplazarse del municipio de Tumaco hacia Bogotá entre el 30 de enero y el 2 de febrero de 2013.
 
 Otras coberturas espaciales mencionadas en el contenido del recurso: San Andrés de Tumaco', remitente_organizacion='Jurisdicción Especial para la Paz - JEP', caracterizacion_temporalidad='1997/01/01|2013/01/01' where codigo='119-CI-00731';
