update esclarecimiento.casos_informes set titulo='Política de la Policía Nacional para confrontar a los grupos armados ilegales con tendencia contrainsurgente en Colombia', autor='Policía Nacional de Colombia', descripcion='Este documento presenta un análisis de la actuación histórica de la Policía Nacional de Colombia frente a las estructuras ilegales denominadas paramilitares, en el marco del conflicto armado interno colombiano, entre los años 1970 y 2005.
  
