update esclarecimiento.casos_informes set titulo='Modelo de construcción de paz de la Policía Nacional: por una Colombia segura y en paz', autor='Policía Nacional de Colombia', descripcion='Este documento es el resultado del proceso fundamentado en la teoría social y la experiencia vital e institucional de los participantes, por medio de una práctica de transformación de conflictos y construcción de paz, posterior a la firma del Acuerdo Final.
  
