update esclarecimiento.casos_informes set titulo='Linea de investigación: financiación y administración de recursos de las FARC-EP', autor='Jurisdicción Especial para la Paz - JEP', descripcion='Línea de investigación: Financiación y administración de recursos de las FARC-EP. Objetivo: Identificar y documentar las formas de obtención de recursos que utilizaron las FARC-EP para la implementación de sus planes de guerra y proyectos políticos, haciendo énfasis en las actividades relacionadas con el narcotráfico y la extorsión; y teniendo en cuenta el tipo de presencia que hizo el grupo armado en cada una de las regiones del país donde operó y las periodizaciones que se hagan en función de las actividades de financiamiento.', remitente_organizacion='Jurisdicción Especial para la Paz - JEP', caracterizacion_temporalidad='1958/01/01|2016/01/01' where codigo='066-CI-01308';
